w3_infura = web3.Web3(
    web3.Web3.HTTPProvider(
        f"https://mainnet.infura.io/v3/{os.environ['WEB3_INFURA_PROJECT_ID']}",
    ),
)


def init_contract(address: str, abi=None):

    address = web3.Web3.toChecksumAddress(address)
    if not abi:
        abi = EtherscanContract(
            address=address,
            api_key=os.environ["ETHERSCAN_API_KEY"],
        ).get_abi()
    contract = w3_infura.eth.contract(address=address, abi=abi)
    return contract


