async def send_update():

    veCRV_balance_wei = veCRV_contract.functions.locked(
        convex_voterproxy_addr
    ).call()[0]
    veCRV_balance = veCRV_balance_wei
    vlCVX_balance = vlCVX_contract.functions.lockedSupply().call()

    veCRV_power = round(veCRV_balance/vlCVX_balance, 2)

    nickname = (
        f"{veCRV_power}"
    )
    activity_status = "veCRV/vlCVX"
    await bot.get_guild(guild_id).me.edit(nick=nickname)
    await bot.change_presence(
        activity=discord.Activity(
            type=discord.ActivityType.watching, name=activity_status
        )
    )
    await asyncio.sleep(60)  # in seconds


@bot.event
async def on_ready():
    """
    When discord client is ready
    """
    while True:
        await send_update()


bot.run(token)
